  const [peliculasFiltradas, setPeliculasFiltradas] = useState([]); //[cite: 311]
  const [busqueda, setBusqueda] = useState(''); //[cite: 312]
  const [modoDescripcion, setModoDescripcion] = useState(false); //[cite: 313]
  const [recomendacion, setRecomendacion] = useState(''); //[cite: 314]

  // Efecto secundario para cargar las pel√≠culas, como se describe en el documento
  useEffect(() => { //[cite: 315]
    fetch('/api/peliculas')
      .then(res => res.json())
      .then(data => {
        setPeliculas(data); //[cite: 316]
        setPeliculasFiltradas(data); //[cite: 316]
      })
      .catch(err => console.error('Error al obtener pel√≠culas:', err)); //[cite: 315]
  }, []); // El array de dependencias vac√≠o asegura que se ejecuta solo al montar [cite: 316]

  // Funciones de b√∫squeda, como se describen en el documento
  const handleBuscar = (e) => { //[cite: 317]
    e.preventDefault();
    const texto = busqueda.toLowerCase();
    const resultado = peliculas.filter(p =>
      p.titulo.toLowerCase().includes(texto) ||
      p.genero.toLowerCase().includes(texto) ||
      p.titulo.toLowerCase().startsWith(texto)
    ); //[cite: 318]
    setPeliculasFiltradas(resultado); //[cite: 318]
    setRecomendacion(''); //[cite: 318]
  };

  const handleBuscarPorDescripcion = async () => { //[cite: 319]
    try {
      const res = await fetch('/api/recomendaciones', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: `Dame una recomendaci√≥n basada en esta descripci√≥n: ${busqueda}. Usa solo pel√≠culas de este cat√°logo: ${peliculas.map(p => p.titulo).join(', ')}.` })
      });
      const data = await res.json(); //[cite: 320]
      setRecomendacion(data.recomendacion); //[cite: 320]
      const seleccionadas = peliculas.filter(p =>
        data.recomendacion.toLowerCase().includes(p.titulo.toLowerCase())
      ); //[cite: 320]
      if (seleccionadas.length > 0) { //[cite: 321]
        setPeliculasFiltradas(seleccionadas); //[cite: 321]
      }
    } catch (err) {
      console.error('Error con IA:', err); //[cite: 321]
    }
  };

  return (
    <div className="App">
      <h1>CineVerse</h1>
      <form className="buscador" onSubmit={handleBuscar}> //[cite: 326]
        <input
          type="text"
          placeholder={modoDescripcion ? 'Describe la peli que buscas...' : 
            'Busca por t√≠tulo o g√©nero'} //[cite: 327]
          value={busqueda}
          onChange={e => setBusqueda(e.target.value)} //[cite: 327]
        />
        {/* Aqu√≠ puedes a√±adir los botones condicionales seg√∫n el modo, como se describe en el documento */}
      </form>
      {recomendacion && ( //[cite: 327]
        <div className="bloque-recomendaciones">
          <h2>IA sugiere:</h2>
          <p>{recomendacion}</p>
        </div>
      )}
      <div className="grid">
        {peliculasFiltradas.map((p, i) => ( //[cite: 327]
          <div className="tarjeta" key={i}>
            <img src={p.poster} alt={p.titulo} /> 
            <div className="info">
              <h3>{p.titulo}</h3> 
              <p>{p.genero}</p> 
              <span>{p.descripcion?.slice(0, 60)}...</span> 
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App; // Exporta App como el componente por defecto
