import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // Estados del componente
  const [peliculas, setPeliculas] = useState([]);
  const [peliculasFiltradas, setPeliculasFiltradas] = useState([]);
  const [busqueda, setBusqueda] = useState('');
  const [modoDescripcion, setModoDescripcion] = useState(false);
  const [recomendacion, setRecomendacion] = useState('');
  const [loading, setLoading] = useState(true); // Nuevo estado para controlar la carga

  // Efecto para cargar las películas desde el backend al montar el componente
  useEffect(() => {
    setLoading(true); // Inicia el estado de carga
    fetch('/api/peliculas')
      .then(res => {
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        return res.json();
      })
      .then(data => {
        console.log('Películas obtenidas del backend:', data); // Log para depuración
        setPeliculas(data);
        setPeliculasFiltradas(data);
      })
      .catch(err => {
        console.error('Error al obtener películas:', err); // Log de error
        setPeliculas([]); // En caso de error, asegura que las listas estén vacías
        setPeliculasFiltradas([]);
      })
      .finally(() => {
        setLoading(false); // Finaliza el estado de carga
      });
  }, []); // El array de dependencias vacío asegura que se ejecute solo una vez al montar

  // Función para manejar la búsqueda tradicional por título o género
  const handleBuscar = (e) => {
    e.preventDefault(); // Previene el comportamiento por defecto del formulario
    const texto = busqueda.toLowerCase();
    const resultado = peliculas.filter(p =>
      p.titulo.toLowerCase().includes(texto) ||
      p.genero.toLowerCase().includes(texto) ||
      p.titulo.toLowerCase().startsWith(texto)
    );
    setPeliculasFiltradas(resultado);
    setRecomendacion(''); // Limpia cualquier recomendación previa de IA
  };

  // Función para manejar la búsqueda por descripción usando la IA
  const handleBuscarPorDescripcion = async () => {
    try {
      // Realiza una petición POST al endpoint de recomendaciones del backend
      const res = await fetch('/api/recomendaciones', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // Envía el prompt a la IA con la descripción del usuario y el catálogo de películas
        body: JSON.stringify({ prompt: `Dame una recomendación basada en esta descripción: ${busqueda}. Usa solo películas de este catálogo: ${peliculas.map(p => p.titulo).join(', ')}.` })
      });
      const data = await res.json();
      setRecomendacion(data.recomendacion); // Almacena la recomendación de la IA

      // Filtra las películas que coincidan con la recomendación de la IA
      const seleccionadas = peliculas.filter(p =>
        data.recomendacion.toLowerCase().includes(p.titulo.toLowerCase())
      );
      if (seleccionadas.length > 0) {
        setPeliculasFiltradas(seleccionadas); // Actualiza las películas filtradas
      } else {
        setPeliculasFiltradas([]); // Si no hay coincidencias, muestra una lista vacía
      }
    } catch (err) {
      console.error('Error con IA:', err); // Manejo de errores en la llamada a la IA
      setRecomendacion('Error al obtener recomendación de IA.');
      setPeliculasFiltradas([]);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>CineVerse</h1>
      </header>
      <nav className="navbar">
        <form className="buscador" onSubmit={handleBuscar}>
          <input
            type="text"
            placeholder={modoDescripcion ?
              'Describe la peli que buscas...' :
              'Busca por título o género'}
            value={busqueda}
            onChange={e => setBusqueda(e.target.value)}
          />
          {/* Botones condicionales según el modo de búsqueda */}
          {modoDescripcion ? (
            <button type="button" onClick={handleBuscarPorDescripcion}>
              Buscar con IA
            </button>
          ) : (
            <button type="submit">Buscar</button>
          )}
          {/* Botón para alternar entre modo tradicional y modo IA */}
          <button type="button" onClick={() => setModoDescripcion(!modoDescripcion)}>
            {modoDescripcion ? 'Modo Tradicional' : 'Modo IA'}
          </button>
        </form>
      </nav>

      {/* Bloque para mostrar la recomendación de la IA si existe */}
      {recomendacion && (
        <div className="bloque-recomendaciones">
          <h2>IA sugiere:</h2>
          <p>{recomendacion}</p>
        </div>
      )}

      <div className="grid">
        {loading ? (
          <p>Cargando películas...</p>
        ) : peliculasFiltradas.length > 0 ? (
          peliculasFiltradas.map((p, i) => (
            <div className="tarjeta" key={i}>
              <img src={p.poster} alt={p.titulo} />
              <div className="info">
                <h3>{p.titulo}</h3>
                <p>{p.genero}</p>
                {/* Limita la descripción a 60 caracteres y añade "..." */}
                <span>{p.descripcion?.slice(0, 60)}...</span>
              </div>
            </div>
          ))
        ) : (
          <p>No se encontraron películas.</p>
        )}
      </div>
    </div>
  );
}

export default App;
