import React, { useState, useEffect } from 'react';
import './App.css'; // Aseg√∫rate de que este archivo exista en tu proyecto

function App() {
  // Estados para almacenar las pel√≠culas, el filtro de b√∫squeda, el modo de b√∫squeda y la recomendaci√≥n de IA
  const [peliculas, setPeliculas] = useState([]);
  const [peliculasFiltradas, setPeliculasFiltradas] = useState([]);
  const [busqueda, setBusqueda] = useState('');
  const [modoDescripcion, setModoDescripcion] = useState(false); // false para b√∫squeda tradicional, true para IA
  const [recomendacion, setRecomendacion] = useState('');

  // useEffect para cargar las pel√≠culas desde el backend cuando el componente se monta
  useEffect(() => {
    // Realiza una petici√≥n GET a la API de pel√≠culas de tu backend
    fetch('/api/peliculas')
      .then(res => res.json()) // Convierte la respuesta a JSON
      .then(data => {
        setPeliculas(data); // Guarda todas las pel√≠culas obtenidas
        setPeliculasFiltradas(data); // Inicialmente, las pel√≠culas filtradas son todas las pel√≠culas
      })
      .catch(err => console.error('Error al obtener pel√≠culas:', err)); // Manejo de errores
  }, []); // El array de dependencias vac√≠o asegura que este efecto se ejecute solo una vez (al montar)

  // Funci√≥n para manejar la b√∫squeda tradicional (por t√≠tulo o g√©nero)
  const handleBuscar = (e) => {
    e.preventDefault(); // Previene el comportamiento por defecto del formulario
    const texto = busqueda.toLowerCase(); // Convierte el texto de b√∫squeda a min√∫sculas
    // Filtra las pel√≠culas que coincidan con el t√≠tulo, g√©nero o que empiecen con el texto
    const resultado = peliculas.filter(p =>
      p.titulo.toLowerCase().includes(texto) ||
      p.genero.toLowerCase().includes(texto) ||
      p.titulo.toLowerCase().startsWith(texto)
    );
    setPeliculasFiltradas(resultado); // Actualiza el estado de las pel√≠culas filtradas
    setRecomendacion(''); // Limpia cualquier recomendaci√≥n de IA anterior
  };

  // Funci√≥n para manejar la b√∫squeda por descripci√≥n usando IA
  const handleBuscarPorDescripcion = async () => {
    try {
      // Realiza una petici√≥n POST a la API de recomendaciones de IA de tu backend
      const res = await fetch('/api/recomendaciones', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // Env√≠a un prompt a la IA con la descripci√≥n de b√∫squeda y el cat√°logo de pel√≠culas
        body: JSON.stringify({ prompt: `Dame una recomendaci√≥n basada en esta descripci√≥n: ${busqueda}. Usa solo pel√≠culas de este cat√°logo: ${peliculas.map(p => p.titulo).join(', ')}.` })
      });
      const data = await res.json(); // Convierte la respuesta a JSON
      setRecomendacion(data.recomendacion); // Guarda la recomendaci√≥n generada por la IA

      // Filtra las pel√≠culas cuyo t√≠tulo est√© contenido en la recomendaci√≥n de la IA
      const seleccionadas = peliculas.filter(p =>
        data.recomendacion.toLowerCase().includes(p.titulo.toLowerCase())
      );

      // Si hay pel√≠culas seleccionadas, actualiza el estado de las pel√≠culas filtradas
      if (seleccionadas.length > 0) {
        setPeliculasFiltradas(seleccionadas);
      } else {
        // Si no hay coincidencias directas, puedes decidir qu√© mostrar (por ejemplo, todas las pel√≠culas o un mensaje)
        setPeliculasFiltradas([]); // Opcional: mostrar un array vac√≠o si no hay coincidencias
      }
    } catch (err) {
      console.error('Error con IA:', err); // Manejo de errores en la petici√≥n a la IA
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>CineVerse</h1>
        <form className="buscador" onSubmit={modoDescripcion ? null : handleBuscar}>
          <input
            type="text"
            placeholder={modoDescripcion ? 'Describe la peli que buscas...' : 'Busca por t√≠tulo o g√©nero'}
            value={busqueda}
            onChange={e => setBusqueda(e.target.value)}
          />
          <div className="botones-busqueda">
            {/* Bot√≥n de b√∫squeda tradicional o por IA, seg√∫n el modo seleccionado */}
            {!modoDescripcion ? (
              <button type="submit">Buscar</button>
            ) : (
              <button type="button" onClick={handleBuscarPorDescripcion}>Buscar por IA</button>
            )}
            {/* Bot√≥n para alternar entre los modos de b√∫squeda */}
            <button
              type="button"
              onClick={() => setModoDescripcion(!modoDescripcion)}
            >
              Cambiar a b√∫squeda {modoDescripcion ? 'tradicional' : 'por IA'}
            </button>
          </div>
        </form>
        {/* Muestra la recomendaci√≥n de IA si existe */}
        {recomendacion && (
          <div className="bloque-recomendaciones">
            <h2>IA sugiere:</h2>
            <p>{recomendacion}</p>
          </div>
        )}
      </header>

      <main className="grid">
        {/* Mapea y renderiza las pel√≠culas filtradas */}
        {peliculasFiltradas.map((p, i) => (
          <div className="tarjeta" key={i}>
            {/* Muestra el p√≥ster de la pel√≠cula o un placeholder si no hay imagen */}
            <img
              src={p.poster || `https://placehold.co/200x300/cccccc/333333?text=${p.titulo}`}
              alt={p.titulo}
              className="rounded-lg shadow-md"
            />
            <div className="info">
              <h3>{p.titulo}</h3>
              <p>{p.genero}</p>
              {/* Muestra una sinopsis limitada a 60 caracteres */}
              <span>{p.descripcion?.slice(0, 60)}...</span>
            </div>
          </div>
        ))}
      </main>
    </div>
  );
}

export default App; // <<-- ¬°¬°¬°Esta l√≠nea es crucial!!!
